var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de cécité
O2.extendClass('ADV.Effect.Blindness', Effect, {
	
	__construct: function() {
		O2.parent(this, 'Blindness', 'curse');
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('blind', 1);
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('blind', -1);
	}
});

module.exports = ADV.Effect.Blindness;
