var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de Clairvoyance
O2.extendClass('ADV.Effect.Clairvoyance', Effect, {
	
	__construct: function() {
		O2.parent(this, 'Clairvoyance', 'bless');
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('clairvoyance', 1);
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('clairvoyance', -1);
	}
});

module.exports = ADV.Effect.Clairvoyance;
