var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de confusion
O2.extendClass('ADV.Effect.Confusion', Effect, {
	
	__construct: function() {
		O2.parent(this, 'Confusion', 'curse');
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('confused', 1);
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('confused', -1);
	}
});

module.exports = ADV.Effect.Confusion;
