var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de modification de la frÃ©quence de tir
// la valeur de l'effet s'exprime en pourcentage
O2.extendClass('ADV.Effect.Cooldown', Effect, {
	
	__construct: function(value) {
		O2.parent(this, 'Cooldown', value > 0 ? 'curse' : 'bless');
		this.setLevel(value);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('cooldown', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('cooldown', -this.getLevel());
	}
});

module.exports = ADV.Effect.Cooldown;
