var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// ESP - Extra Sensory Perception
O2.extendClass('ADV.Effect.ESP', Effect, {
	
	__construct: function() {
		O2.parent(this, 'ESP', 'bless');
		this.setLevel(1);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('esp', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('esp', -this.getLevel());
	}
});

module.exports = ADV.Effect.ESP;
