var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Energy - Diminue le temps de chargemebnt des baguettes
var ADV_Effect_Energy = O2.extendClass(Effect, {
	
	__construct: function(nLevel) {
		O2.parent(this, 'Energy', nLevel > 0 ? 'bless' : 'curse');
		this.setLevel(nLevel);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('energy', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('energy', -this.getLevel());
	}
});

module.exports = ADV_Effect_Energy;
