var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Invisibility
// Attention l'invisibilité est brisée lorsqu'on tire
O2.extendClass('ADV.Effect.Invisibility', Effect, {
	
	__construct: function() {
		O2.parent(this, 'Invisibility', 'bless');
		this.setLevel(1);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('invisible', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('invisible', -this.getLevel());
	}
});

module.exports = ADV.Effect.Invisibility;
