var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Luck - Good luck = better loots
O2.extendClass('ADV.Effect.Luck', Effect, {
	
	__construct: function(nLevel) {
		O2.parent(this, 'Luck', nLevel > 0 ? 'bless' : 'curse');
		this.setLevel(nLevel);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('luck', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('luck', -this.getLevel());
	}
});

module.exports = ADV.Effect.Luck;
