var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

O2.extendClass('ADV.Effect.Pharma', Effect, {
	__construct: function(nLevel) {
		O2.parent(this, 'Pharma', nLevel > 0 ? 'bless' : 'curse');
		this.setLevel(nLevel);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('pharma', this.getLevel());
	},
	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('pharma', -this.getLevel());
	}
});

module.exports = ADV.Effect.Pharma;
