var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de purge des effets n√©faste
O2.extendClass('ADV.Effect.Remedy', Effect, {
	
	__construct: function(n) {
		O2.parent(this, 'Remedy', 'bless');
		this.setLevel(n);
	},
	
	cast: function(ep) {
		this.getTarget()
			.getSoul()
			._aEffects
			.filter(function(e) {
				return e.hasTag('curse');
			})
			.slice(0, this.getLevel())
			.forEach(function(e) {
				e.dispel();
			});
		ep.removeDeadEffects();
	},
});

module.exports = ADV.Effect.Remedy;
