var O2 = require('o2');
var Effect = require('./Abstract/BreakableEffect.js');

// Effet de paralyzie
// L'effet est brisable lorsque la creature subit des dégâts
var ADV_Effect_Root = O2.extendClass(Effect, {
	__construct: function(nStrength) {
		O2.parent(this, 'Root', 'curse');
		this.setLevel(nStrength);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('root', 1);
	},
	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('root', -1);
	}
});

module.exports = ADV_Effect_Root;
