var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de ponction de vie lors des dégats infligé à une entité
O2.extendClass('ADV.Effect.Vampyre', Effect, {
	
	__construct: function(nPower) {
		O2.parent(this, 'Vampyre', 'bless');
		this.setLevel(nPower);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('vampyre', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('vampyre', -this.getLevel());
	}
});

module.exports = ADV.Effect.Vampyre;
