var DATA = require('./loader.js')(__dirname + '/Items/');

function createItem(sItem) {
	if (sItem in DATA) {
		var oItem = new DATA[sItem]();
		oItem.resref = sItem;
		return oItem; 
	} else {
		throw new Error('Items.create : unknown resref : ' + sItem); 
	}
}

module.exports = {
	create: createItem
};
