var O2 = require('o2');
var GCItem = require('gc').Item;

var ADV_Item = O2.extendClass(GCItem, {
	
	type: '',	// type d'item
	
	action: function(ctx) {
		switch (ctx.action) {
			case 'fire':
				if (this.cool(ctx)) {
					this.fire(ctx);
				}
				break;
				
			case 'use':
				this.use(ctx);
				break;

			case 'misuse':
				this.misuse(ctx);
				break;
		}
	}
});

module.exports = ADV_Item;
