var O2 = require('o2');
var Item = require('./Item.js');

/**
 * Potion de base
 */
var Potion = O2.extendClass(Item, {

	type: 'potion',
	frozen: false,

	use: function(ctx) {
		if (!this.frozen) {
			this.drink(ctx);
			ctx.consume = true;
		} else {
			ctx.consume = false;
		}
	},
	
	misuse: function(ctx) {
		if (!this.frozen) {
			this.drink(ctx);
			ctx.consume = true;
		} else {
			ctx.consume = false;
		}
	}
});

module.exports = Potion;
