var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Book = require('./Abstract/Book.js');
var u = require('../Utils.js');

/**
 * spellbook of amnesia
 * Le cooldown de tous les livres de sort de l'adversaire sont remis à FULL
 * Cela empèche les cibles de pouvoir réutiliser les sort avant un certain temps
 */
var BookAmnesia = O2.extendClass(Book, {
	cooldown: CONST.SPELL_DURATION_LEVEL[4],

	read: function(ctx) {
		var eAmnesia = new Effect.Amnesia(10);
		this.curse(eAmnesia);
	}
});

module.exports = BookAmnesia;
