var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Book = require('./Abstract/Book.js');
var u = require('../Utils.js');

/**
 * spellbook of deathspell
 * Ce sort envoie une gerbe de 5 projectiles meurtriers ayant chacun le potentiel de tuer
 * la creature qui aura le malheur de se le prendre dans la tronche. 
 */
O2.extendClass('BookDeathSpell', Book, {
	missileRef: 'p_spell_c2',
	missiles: 5,
	cooldown: CONST.SPELL_DURATION_LEVEL[6],

	read: function(ctx) {
		var eDeath = new Effect.Death();
		this.curse(eDeath);
	}
});

module.exports = BookDeathSpell;
