var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Book = require('./Abstract/Book.js');

/**
 * spellbook of nullity sphere
 * Le sort dresse une barriere de protection conférant invulnerabilité aux dégats et immunité aux sorts
 * de la à dire que le beneficiaire est invulnerable, cela reste a vérifier. mais en tout cas c'est une protection
 * tres efficace contre la plusart des attaques.
 * l'effet dure peu de temps : a peine 6 secondes
 */
var BookNullitySphere = O2.extendClass(Book, {
	cooldown: CONST.SPELL_DURATION_LEVEL[5],
	
	read: function(ctx) {
		var eDef = new Effect.Defense(100);
		eDef.setDuration(CONST.EFFECT_DURATION_VERY_SHORT);
		this.bless(eDef);
	}
});

module.exports = BookNullitySphere;
