var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion d'energie :
 * Cette potion amplifie la production d'energie magique necessaire au fonctionement de la baguette.
 * Cela ce traduit par une diminution notable du temps de chargement necessaire pour tirer une gerbe de projectiles
 */
var PotionEnergy = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eEnergy = new Effect.Energy(40);
		eEnergy.setDuration(CONST.EFFECT_DURATION_LONG);
		ctx.effects = [eEnergy];
	}
});

module.exports = PotionEnergy;

