var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de perception extra sensorielle
 * Ceci permet tout simplement de distinguer les creatures invisibles.
 * C'est à dire celles qui ont utilisé la potion d'invisibilité
 */
var PotionEsp = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eESP = new Effect.ESP();
		eESP.setDuration(CONST.EFFECT_DURATION_LONG);
		ctx.effects = [eESP];
	}
});

module.exports = PotionEsp;

