var O2 = require('o2');
var u = require('../Utils.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de soin :
 * Soigne de 25 Ã  35 points de vie.
 */
O2.extendClass('PotionHealing', Potion, {
	drink: function(ctx) {
		var eHeal = new Effect.Heal(u.rand(25, 35));
		ctx.effects = [eHeal];
	}
});

module.exports = PotionHealing;
