var O2 = require('o2');
var u = require('../Utils.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de soin 2 :
 * Soigne de 75 Ã  100 points de vie.
 */
O2.extendClass('PotionHealing2', Potion, {
	drink: function(ctx) {
		var eHeal = new Effect.Heal(u.rand(75, 100));
		ctx.effects = [eHeal];
	}
});

module.exports = PotionHealing2;
