var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion d'immortalit√© :
 * Cette potion amplifie la production d'energie magique necessaire au fonctionement de la baguette.
 * Cela ce traduit par une diminution notable du temps de chargement necessaire pour tirer un triple projectile
 */
var PotionImmortal = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eImmortal = new Effect.Immortal();
		eImmortal.setDuration(CONST.EFFECT_DURATION_SHORT);
		ctx.effects = [eImmortal];
	}
});

module.exports = PotionImmortal;

