var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de fortune :
 * Augmente la chance de trouver des objets suppl√©mentaire dans les coffre
 */
var PotionLuck = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eLuck = new Effect.Luck(25);
		eLuck.setDuration(CONST.EFFECT_DURATION_LONG);
		ctx.effects = [eLuck];
	}
});

module.exports = PotionLuck;

