var O2 = require('o2');
var u = require('../Utils.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de poison :
 * inflige de 25 à 35 de dégât de points de vie à l'abruti qui boit cette potion.
 */
var PotionPoison = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eDmg = new Effect.Damage(u.rand(25, 35));
		ctx.effects = [eDmg];
	}
});

module.exports = PotionPoison;
