var O2 = require('o2');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Remède :
 * Celui qui boit cette potion guérit de 4 effets néfastes temporaire.
 */
var PotionRemedy = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eRem = new Effect.Remedy(4);
		ctx.effects = [eRem];
	}
});

module.exports = PotionRemedy;
