var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Parchemin de confusion :
 * La precision de la cible est reduite. et lire un parchemin devient dangeureux
 */
O2.extendClass('ScrollConfusion', Scroll, {
	read: function(ctx) {
		var eConf = new Effect.Confusion();
		eConf.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse(eConf);
	}
});

module.exports = ScrollConfusion;
