var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Scroll of Magnet :
 */
O2.extendClass('ScrollDispel', Scroll, {
	read: function(ctx) {
		var eDispel = new Effect.Dispel(5);
		this.curse(eDispel);
	}
});

module.exports = ScrollDispel;
