var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Parchemin de Drain Life :
 * Le lanceur de sort bénéficie d'un bonus de vampirisme : Tous les dégâts qu'il infligent aux autres
 * lui permette de regagner des points de vie à hauteur de 50% des points de dégâts qu'il a infligé.
 */
var ScrollDrainLife = O2.extendClass(Scroll, {
	read: function(ctx) {
		var eDrain = new Effect.Vampyre(50);
		eDrain.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.bless(eDrain);
	}
});

module.exports = ScrollDrainLife;
