var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Scroll of  Power :
 * Le lanceur de sort bénéficie d'un bonus de puissance de feu.
 * Les projectile tirés par sa baguette bénéficie d'une puissance de feu +33%
 * Et d'une durée de +33%
 */
O2.extendClass('ScrollPower', Scroll, {
	read: function(ctx) {
		var ePow = new Effect.Power(33);
		ePow.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.bless(ePow);
	}
});

module.exports = ScrollPower;
