var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * 
 */
O2.extendClass('ScrollReflect', Scroll, {
	read: function(ctx) {
		var eReflect = new Effect.Reflect(50);
		eReflect.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.bless(eReflect);
	}
});

module.exports = ScrollReflect;
