var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');
var u = require('../Utils.js');

/**
 * Parchemin d'ancrage:
 * La cible visée par ce sort est clouée au sol et plus se déplacer. Elle peut tout de même tourner, tirer et utiliser des objets.
 * Le sort peut être brisé et la cible libérée si celle-ci est frappée par des projectiles.
 * (entre 5 et 10 projectile suffisent à briser le sort).
 */
var ScrollRoot = O2.extendClass(Scroll, {
	read: function(ctx) {
		var eRoot = new Effect.Root(u.rand(5, 10));
		eRoot.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse(eRoot);
	}
});

module.exports = ScrollRoot;
