var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Parchemin de faibless :
 * Puissance de feu -25%
 * Defense -25%
 */
O2.extendClass('ScrollWeakness', Scroll, {
	read: function(ctx) {
		var ePow = new Effect.Power(-25);
		ePow.setDuration(CONST.EFFECT_DURATION_SHORT);
		var eDef = new Effect.Defense(-25); 
		eDef.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse([ePow, eDef]);
	}
});

module.exports = ScrollWeakness;
