var O2 = require('o2');
var MissileThinker = require('./MissileThinker.js');
var u = require('mwgame/Utils.js');
var CONST = require('mwgame/data/consts.js');


var SlashThinker = O2.extendClass(MissileThinker, {
	
	_oOwner: null,
	_bInCannon: true,
	_nRange: 4,

	thinkMove: function() {
		O2.parent(this);
		if (--this._nRange <= 0) {
			this.oEntity.bActive = true;
		}
	},
});

module.exports = SlashThinker;
