var _rnd = Math.random;

/**
 * Nombre entier aléatoire entre n1 et n2
 * @param int n1 borne inférieure
 * @param int n2 borne supérieure
 * @return int
 */
function rand(n1, n2) {
	if (n1 === undefined) {
		return _rnd();
	} else {
		if (n2 === undefined) {
			throw new Error('two parameters needed, only one has been specified');
		}
		return (_rnd() * (n2 - n1 + 1) | 0) + n1;
	}
}

/**
 * Détermine si un évènement qui a une probabilité de se produire
 * se produit
 * renvoie true si oui
 */
function prob(nProb) {
	return rand(0, 99) < nProb;
}


/**
 * Selectionne un élement aléatoirement dans le tableau
 * @param aArray
 */
function choose(aArray) {
	return aArray[rand(0, aArray.length - 1)];
}




///

/** Calcul de la distance entre deux point y
 * @param dx delta x
 * @param dx delta y
 * @return float
 */
function distance(ax, ay, bx, by) {
	var dx = ax - bx;
	var dy = ay - by;
	return Math.sqrt((dx * dx) + (dy * dy));
}





module.exports = {
	rand: rand,
	prob: prob,
	choose: choose,
	distance: distance
};
