/**
 * Chargment des plugin qui sont dans mwplugins
 */
var fs = require('fs');

function loadPlugins() {
	var aMods = fs.readdirSync(__dirname + '/../mwplugins');
	var oObjects = {};
	aMods.forEach(function(f) {
		oObjects[f] = require(__dirname + '/../mwplugins/' + f);
	});
	return oObjects;
}

module.exports = loadPlugins();
