test('basic', function(assert) {
	var c = new Client();
	assert.ok(c, 'client instanciation');
	c.setId(5);
	assert.equal(c.getId(), 5, 'id setter / getter');
	c.setName('ralphy');
	assert.equal(c.getName(), 'ralphy', 'name setter / getter');
	assert.throws(function() {
		c.setName('b');
	}, 'name too short');
	c.setSocket({r: 5});
	assert.equal(c.getSocket().r, 5, 'socket setter / getter');
	c.setInstance({i: 8});
	assert.equal(c.getInstance().i, 8, 'isntance setter / getter');
	
	var oDataTest = {};
	var e = {
		setData: function(x, y) {
			oDataTest[x] = y;
		}
	};
	
	c.setEntity(e);
	assert.equal(oDataTest['idClient'], 5, 'client id store in entity');
});

