test('basic', function(assert) {
	var e = new ADV.Effect('truc');
	assert.ok(e);
	
	assert.deepEqual(e._aTags, ['truc']);
	e.setTag('machin');
	e.setTag('chose');
	assert.deepEqual(e._aTags, ['truc', 'machin', 'chose']);
	assert.ok(e.hasTag('chose'));
	assert.ok(e.hasTag('machin'));
	assert.ok(!e.hasTag('bidule'));
});


