test('basic', function(assert) {
	var c = new ADV.Soul();
	assert.ok(c);
});

test('setting and getting attributes', function(assert) {
	var c = new ADV.Soul();
	assert.equal(c.getAttribute('life'), 0, 'no life attribute yet');
	c.setAttribute('life', 100);
	assert.equal(c.getAttribute('life'), 100, '100 life point initial attribute');
	c.setBonus('life', 30);
	assert.equal(c.getAttribute('life'), 130, '130 life point with bonus');
	assert.deepEqual(c._oAttributes, { 'life': 100 });
	assert.deepEqual(c._oBonuses, { 'life': 30 });
	c.setBonus('magic', 50);
	assert.equal(c.getAttribute('magic'), 50, 'only bonus here');
	assert.deepEqual(c._oAttributes, { 'life': 100 });
	assert.deepEqual(c._oBonuses, { 'life': 30, 'magic': 50 });
	c.setBonus('magic', 10);
	c.setBonus('life', 15);
	assert.deepEqual(c._oBonuses, { 'life': 45, 'magic': 60 });
});
